import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

// class Square extends React.Component { // class-based Square

//     render() {
//       return (
//         <button
//             className="square"
//             onClick={() => this.props.onClick()}
//         >
//             {this.props.value}
//         </button>
//       );
//     }
//   }

// function-based Square because no state & only renders
function Square(props) {
    return (
      <button className="square" onClick={props.onClick}>
        {props.value}
      </button>
    );
  }

  class Board extends React.Component {
    constructor(props) { // take in props
        super(props);// pass props to parent constructor
        this.state = {// initialize out state
          squares: Array(9).fill(null),
        };
      }

      // handleClick will live here because state is here
      handleClick(i) {

        // cannot do this in reaft - state is immutable
        // this.state.squares[i] = 'X'; (cannot)
          // using slice to copy the array - cloning
        const squares = this.state.squares.slice();
        squares[i] = 'X';
        this.setState({squares: squares});
      }

    renderSquare(i) {
        return <Square 
            value={this.state.squares[i]} 
            onClick={() => this.handleClick(i)}
        />;
    }

    render() {
      const status = 'Next player: X';

      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
        </div>
      );
    }
  }

  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  // ========================================

  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
